mapper.delete:
delete
from videoClick
where userId = #{userId}
  and videoId = #{videoId}

mapper.select:
select count(*)
from videoClick
where videoId = #{videoId}
  and userId = #{userId}
  and clickType = 0

select count(*)
from videoCoin
where videoId = #{videoId}
  and userId = #{userId}

select COALESCE(sum(coinCount), 0)
from videoCoin
where videoId = #{videoId}
