mapper.select:
select *
from user
where userPetName = #{userPetName}
  and userPassword = #{userPassword}

mapper.update:
update user
set userCoin=userCoin - #{coinCount}
where userId = #{userId}

mapper.select:
select count(followUserId)
from follow
where followedUserId = #{userId}

select count(*)
from follow
where followUserId = #{followUserId}
  and followedUserId = #{followedUserId}

mapper.delete:
delete
from follow
where followUserId = #{followUserId}
  and followedUserId = #{followedUserId}

mapper.select:
select count(followedUserId)
from follow
where followUserId = #{userId}

select followedUserId
from follow
where followUserId = #{userId}
